import React, {useState} from 'react';
import "./../Css/NewExpense/ExpenseForm.css";

const ExpenseForm = () => {
    const [enteredDate, setEnteredDate] = useState('');
    const [enteredTitle, setEnteredTitle] = useState('');
    const [enteredAmount, setEnteredAmount] = useState('');

    // const [userInput, setUserInput] = useState({
    //     enteredTitle: '',
    //     enteredAmount: '',
    //     enteredDate: ''
    // })
    // const titleChangeHandler = (event) => {
    //     setUserInput({
    //         ...userInput,
    //         enteredTitle: event.target.value,
    //     })
    // }
    //
    // const amountChangeHandler = (event) => {
    //     setUserInput({
    //         ...userInput,
    //         enteredAmount: event.target.value,
    //     })
    //
    // }
    //
    // const dateChangeHandler = (event) => {
    //   setUserInput({
    //         ...userInput,
    //         enteredDate: event.target.value,
    //   })
    // }
    const inputChangeHandler = (identifier, value) => {

        if (identifier === 'title') {
            console.log(value)
            setEnteredTitle(value)
        } else if (identifier === 'amount') {
            console.log(value)
            setEnteredAmount(value)
        } else {
            console.log(value)
            setEnteredDate(value)
        }

    }
    return (
        <form>
            <div className="new-expense__controls">
                <div className="new-expense__control">
                    <label>Title</label>
                    <input type="text" onChange={(event)=>inputChangeHandler('title',event.target.value)}></input>
                </div>
                <div className="new-expense__control">
                    <label>Amount</label>
                    <input type="number" min="0.01" step="0.01" onChange={(event)=>inputChangeHandler("amount",event.target.value)}></input>
                </div>
                <div className="new-expense__control">
                    <label>Date</label>
                    <input type="date" min="2022-01-01" max="2023-07-01" onChange={(event)=>inputChangeHandler("date",event.target.value)}></input>
                </div>
            </div>
            <div className="new-expense__actions">
                <button type="submit">Add Expense</button>
            </div>
        </form>
    )
}

export default ExpenseForm;
